import React, { useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import Testimonial from './Testimonial';
import './Style.css';

function Homepage() {
  const testimonialsData = [
    {
      rating: 5,
      message: "I can now access my favorite newspaper at only 20 bob! What a big saving for me and for the environment",
      name: "Anonymous"
    },
    {
      rating: 5,
      message: "I always go to the discover tab and I never get disappointed. So much clean content for all audiences",
      name: "Anonymous"
    },
    {
      rating: 5,
      message: "As a small business, I love accessing the M-PESA statement on the Safaricom web to keep close tabs on my transactions",
      name: "Anonymous"
    }
  ];

  const [activeTestimonial, setActiveTestimonial] = useState(0);

  const nextTestimonial = () => {
    setActiveTestimonial((activeTestimonial + 1) % testimonialsData.length);
  };

  const prevTestimonial = () => {
    setActiveTestimonial((activeTestimonial - 1 + testimonialsData.length) % testimonialsData.length);
  };

  return (
    <>
      <Navbar />
      <div className="background-container">
        <div className="testimonials-container">

          <Testimonial
            rating={testimonialsData[activeTestimonial].rating}
            message={testimonialsData[activeTestimonial].message}
            name={testimonialsData[activeTestimonial].name}
          />
          <div className="arrow next" onClick={nextTestimonial}>
            &rarr;
          </div>

        </div>
      </div>
      <br /><br /><br /><br /><br /><br /><br /><br />
      <Footer />
    </>
  );
}

export default Homepage;
