import React, { useState } from 'react'; // Import useState
import Navbar from './Navbar';
import Footer from './Footer';
import Testimonial from './Testimonial';
import './Style.css';

function Homepage() {
    const [currentTestimonial, setCurrentTestimonial] = useState(0); // State to keep track of the current testimonial index

    const testimonials = [
        {
            rating: 5,
            message: "I can now access my favorite newspaper at only 20 bob! What a big saving for me and for the environment",
            name: "Anonymous"
        },
        {
            rating: 5,
            message: "I always go to the discover tab, and I never get disappointed. So much clean content for all audiences",
            name: "Anonymous"
        },
        {
            rating: 5,
            message: "As a small business, I love accessing the M-PESA statement on the Safaricom web to keep close tabs on my transactions",
            name: "Anonymous"
        }
    ];

    const handleNext = () => {
        setCurrentTestimonial((prevIndex) => (prevIndex + 1) % testimonials.length); // Handle next testimonial
    };

    const handlePrev = () => {
        setCurrentTestimonial((prevIndex) => (prevIndex - 1 + testimonials.length) % testimonials.length); // Handle previous testimonial
    };

    return (
        <>
            <Navbar />
            <div className='backgroud-container'>
                <div className='testimonials-container'>
                    <Testimonial
                        rating={testimonials[currentTestimonial].rating}
                        message={testimonials[currentTestimonial].message}
                        name={testimonials[currentTestimonial].name}
                    />
                </div>
                <div>
                    <button onClick={handlePrev}>Previous</button>
                    <button onClick={handleNext}>Next</button>
                </div>
            </div>
            <br /><br /><br /><br /><br /><br /><br /><br />
            <Footer />
        </>
    );
}

export default Homepage;
