        textAlign: 'center',
    };

    const textWithPaddingStyle = {
        padding: '0 10px'
    }

    const lineStyle = {
        borderLeft: '1px solid white',
        margin: 'o 10px',
        height: '1em',
        paddingLeft: '5px',
        paddingRight: '5px',
    }

    return (
        <footer style={footerStyle}>
            <p>&copy; <span >Safaricom PLC </span>Safaricom PLC 
                <span style={lineStyle}></span>

                All Rights Reserved.</p>
        </footer>
    );
}


export default Footer;
